function Break(){document.querySelectorAll("break").forEach(e=>{const t=document.createElement("br");e.parentNode.replaceChild(t,e)})}function ExtraBreak(e=2){document.querySelectorAll("extra-break").forEach(t=>{const r=document.createDocumentFragment();for(let t=0;t<e;t++){const e=document.createElement("br");r.appendChild(e)}t.parentNode.replaceChild(r,t)})}function CustomBreak(){document.querySelectorAll("custom-break").forEach(e=>{const t=document.createDocumentFragment(),r=parseInt(e.getAttribute("n"));for(let e=0;e<r;e++){const e=document.createElement("br");t.appendChild(e)}e.parentNode.replaceChild(t,e)})}document.addEventListener("DOMContentLoaded",()=>{Break(),ExtraBreak(),CustomBreak()});